mapper.insert:

    SELECT SEQ_CALENDAR.NEXTVAL FROM DUAL


INSERT INTO TBL_CALENDAR (
ID, CALENDAR_TITLE, CALENDAR_INDEX, MEMBER_ID
) VALUES (
#{id},
#{calendarTitle},
#{calendarIndex},
#{memberId}
)

mapper.select:
SELECT ID, CALENDAR_TITLE, CALENDAR_INDEX, MEMBER_ID
FROM TBL_CALENDAR
WHERE MEMBER_ID = #{memberId}

SELECT ID, CALENDAR_TITLE, CALENDAR_INDEX, TODOLIST_ID
FROM TBL_CALENDAR
WHERE ID = #{id}

mapper.update:
UPDATE TBL_CALENDAR
SET CALENDAR_TITLE = #{calendarTitle}, CALENDAR_INDEX = #{calendarIndex}
WHERE ID = #{id}
