mapper.select:
SELECT TREE_ID
FROM TBL_TREE
WHERE MEMBER_ID = #{memberId}

SELECT VWT.MEMBER_ID, VWT.ITEM_ID, VWT.TREE_CUSTOMIZING_POSITION_X, VWT.TREE_CUSTOMIZING_POSITION_Y, VWT.ITEM_NAME, VWT.ITEM_TYPE, VWT.ITEM_IMG_PATH, VWT.ITEM_IMG_NAME, VWT.ITEM_SIZE_WIDTH, VWT.ITEM_SIZE_HEIGHT, VWI.TOTAL_COUNT, VWI.APPLIED_COUNT, VWI.NOT_APPLIED_COUNT FROM VW_MEMBER_TREE_ITEM_COUNT_SUMMARY VWI JOIN( SELECT ID, MEMBER_ID, ITEM_ID, TREE_CUSTOMIZING_POSITION_X, TREE_CUSTOMIZING_POSITION_Y, ITEM_NAME, ITEM_TYPE, ITEM_IMG_PATH, ITEM_IMG_NAME, ITEM_SIZE_WIDTH, ITEM_SIZE_HEIGHT FROM ( SELECT ID, MEMBER_ID, ITEM_ID, TREE_CUSTOMIZING_POSITION_X, TREE_CUSTOMIZING_POSITION_Y, ITEM_NAME, ITEM_TYPE, ITEM_IMG_PATH, ITEM_IMG_NAME, ITEM_SIZE_WIDTH, ITEM_SIZE_HEIGHT, ROW_NUMBER() OVER (PARTITION BY MEMBER_ID, ITEM_ID ORDER BY ID) AS RN FROM VW_MEMBER_TREE ) WHERE RN = 1 ) VWT ON VWI.ITEM_ID = VWT.ITEM_ID WHERE VWT.MEMBER_ID = #{memberId}
AND VMT.ITEM_TYPE = #{itemType}
AND VMT.ITEM_TYPE = #{itemType}
AND VMT.ITEM_TYPE = #{itemType}

mapper.update:
UPDATE TBL_TREE_CUSTOMIZING
SET
    TREE_CUSTOMIZING_POSITION_X = #{treeCustomizingPositionX},
    TREE_CUSTOMIZING_POSITION_Y = #{treeCustomizingPositionY},
    TREE_CUSTOMIZING_APPLY = #{treeCustomizingApply}
WHERE ID = #{treeCustomizingId}
