mapper.select:
SELECT DISTINCT TBI.ID AS ITEM_ID, TBI.ITEM_NAME, TBI.ITEM_TYPE, TBI.ITEM_PRICE, TBI.ITEM_IMG_PATH, TBI.ITEM_IMG_NAME, TBI.ITEM_SIZE_WIDTH, TBI.ITEM_SIZE_HEIGHT, CASE WHEN VMT.MEMBER_ID IS NOT NULL THEN 1 ELSE 0 END AS ITEM_OWNED FROM TBL_ITEM TBI LEFT JOIN VW_MEMBER_TREE VMT ON TBI.ID = VMT.ITEM_ID AND VMT.MEMBER_ID = #{memberId}
AND VMT.ITEM_TYPE = #{itemType}
ORDER BY ITEM_OWNED, ITEM_ID

SELECT ID, ITEM_ID, MEMBER_ID, CART_ITEM_COUNT
FROM TBL_CART
WHERE ID = #{id}

mapper.update:
UPDATE TBL_ITEM
ITEM_NAME = #{itemName}, ITEM_TYPE = #{itemType}, ITEM_PRICE = #{itemPrice}, ITEM_IMG_PATH = #{itemImgPath}, ITEM_IMG_NAME = #{itemImgName}, ITEM_SIZE_WIDTH = #{itemSizeWidth}, ITEM_SIZE_HEIGHT = #{itemSizeHeight},
WHERE ID = #{id}

UPDATE TBL_CART
SET CART_ITEM_COUNT = #{cartItemCount}
WHERE ID = #{id}
